import { Component } from "react";

export default class Login extends Component{
    constructor(){
        super();
            this.state = {
                name :'',
                age : 20,
            }
    }
    add= ()=>{
        this.props.added({name:this.state.name, age: this.state.age});
    }
    render(){
        return (
            <div>
                <input type="text" placeholder= "Name" value = {this.state.name}></input>
                <input type="text" placeholder= "Age" value = {this.state.age}></input>
                <button onClick = {this.add}>Join Ninja Turtles</button>
            </div>
       )
    }
}
